package MyPak;

public class Jstar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		
		for(i=1;i<=5;i++)
		{
			System.out.println("");
			for(j=1;j<=i;j++)
			{
				System.out.print("* ");
			}
		}
		
		//System.out.println("");

		
		//Reverse pyramid 
		
		
		  int m,n;
		  
		  for(m=5;m>=1;m--) { System.out.println("");
		  
		  
		  { for(n=m;n>=1;n--) { System.out.print("* "); }
		  
		  }
		  
		  }
		 
	}

}
