package com.Java.Practices;

public class Swap2noswithtemp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=5,y=10,temp;
		System.out.println("X Value is :"+x);
		System.out.println("Y Value is :"+y);

		temp=x;
		x=y;
		y=temp;
		System.out.println("X Value is :"+x);
		System.out.println("Y Value is :"+y);


	}

}
